import "bootstrap/dist/css/bootstrap.css";
import React, { Component } from "react";
import "./studyPage.css";
import DistractionTask from "./DistractionTask";
import MainQuestions from "./MainQuestions";
import { Button } from "react-bootstrap";
import Popup from "reactjs-popup";
import Survey from "../Survey";

class StudyPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      timer: "00:05:30",
      total_time: 330000,
      current_time: 0,
      current_condition: parseInt(this.props.router.params.study_part_i),
      main_timer_id: null,
      user_id: this.props.router.params.id,
      experiment_start: true,
      main_task_results: null,
      distraction_task_results: null,
      survey_results: [],
      survey_timer: 91000,
      current_survey: 0,
      display_survey: false,
    };
  }

  updateMain = (results) => {
    const adapt_state = this.state;
    adapt_state.main_task_results = results;
    this.setState({ adapt_state });
  };

  updateSide = (results) => {
    const adapt_state = this.state;
    adapt_state.distraction_task_results = results;
    this.setState({ adapt_state });
  };

  move_to = (study_part, condition) => {
    this.props.router.navigate(
      "/" +
        this.state.user_id +
        study_part.toString() +
        "/" +
        condition.toString()
    );
  };

  setTimerId = (id) => {
    const adapt_state = this.state;
    adapt_state.main_timer_id = id;
    this.setState({ adapt_state });
  };

  setTimer = (time) => {
    const adapt_state = this.state;
    adapt_state.timer = time;

    this.setState({ adapt_state });
  };

  setStart = (truth) => {
    const adapt_state = this.state;
    adapt_state.experiment_start = truth;

    this.setState({ adapt_state });
  };

  updateCurrentTime = () => {
    if (this.state.current_time === this.state.total_time) {
      clearInterval(this.state.main_timer_id);
    } else {
      const adapt_state = this.state;
      adapt_state.current_time += 1000;
      console.log(adapt_state.current_time);
      this.setState({ adapt_state });
      this.printTime();
    }
  };

  getTimeRemaining = () => {
    const total = this.state.total_time - this.state.current_time;
    const seconds = Math.floor((total / 1000) % 60);
    const minutes = Math.floor((total / 1000 / 60) % 60);
    const hours = Math.floor((total / 1000 / 60 / 60) % 24);
    return {
      total,
      hours,
      minutes,
      seconds,
    };
  };

  printTime = () => {
    let { total, hours, minutes, seconds } = this.getTimeRemaining();
    if (total >= 0) {
      this.setTimer(
        (hours > 9 ? hours : "0" + hours) +
          ":" +
          (minutes > 9 ? minutes : "0" + minutes) +
          ":" +
          (seconds > 9 ? seconds : "0" + seconds)
      );
    }
  };

  pauseTimer = () => {
    clearInterval(this.state.main_timer_id);
    this.setTimerId(null);
  };

  restartTimer = () => {
    const id = setInterval(() => {
      this.updateCurrentTime();
    }, 1000);
    this.setTimerId(id);
  };

  startTimer = () => {
    const id = setInterval(() => {
      this.updateCurrentTime();
    }, 1000);
    this.setTimerId(id);
    this.setStart(false);
  };

  displayStudy = () => {
    if (this.state.experiment_start) {
      return (
        <Button variant="primary" type="submit" onClick={this.startTimer}>
          Start
        </Button>
      );
    } else {
      return (
        <div className="container">
          <Survey
            update_results={this.updateSurvey}
            enabled={this.state.display_survey}
          ></Survey>
          <div className="row">
            <div className="col-sm border-10">
              <DistractionTask
                config={
                  this.props.config[this.state.current_condition]["side-task"]
                }
                time={this.state.current_time}
                update_results={this.updateSide}
              ></DistractionTask>
            </div>
            <div className="col-sm border-10">
              <MainQuestions
                config={
                  this.props.config[this.state.current_condition]["main-task"]
                }
                time={this.state.current_time}
                update_results={this.updateMain}
              ></MainQuestions>
            </div>
          </div>
        </div>
      );
    }
  };

  render() {
    return (
      <div>
        <div className="Timer">
          <h2>{this.state.timer}</h2>
          {this.displayStudy()}
        </div>
      </div>
    );
  }
}

export default StudyPage;
