import React, { Component } from "react";

class Essay extends Component {
  state = {};
  update_answers = (event) => {
    this.props.update_results({
      answer: event.target.value,
      busyness: 3,
      question: this.props.config["source"],
    });
  };

  render() {
    return (
      <div>
        <Form>
          <Form.Group className="mb-3" controlId={"Form.Question"}>
            <Form.Label>
              <div className="ratio ratio-16x9">
                <iframe
                  src={this.props.config["source"]}
                  title="YouTube video"
                  allowFullScreen
                ></iframe>
              </div>
              <div>Please transcribe the following video into text</div>
            </Form.Label>
            <Form.Control
              as="textarea"
              rows={8}
              onChange={this.update_answers}
              disabled={this.props.disabled}
            />
          </Form.Group>
        </Form>
      </div>
    );
  }
}

export default Essay;
