import React, { Component } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import { Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

class Survey extends Component {
  state = {
    results: {
      mental: null,
      temporal: null,
      performance: null,
      effort: null,
      frustration: null,
    },
  };

  displayModal = () => {
    return (
      <div>
        <Modal
          show={this.state.open}
          backdrop="static"
          keyboard={false}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header>
            <Modal.Title>Cognitive Workload Survey</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div>
              Please tell us from a scale of 0 (very low) to 100 (very high),
              how demanding your workload felt in the following aspects:
            </div>
            <div>
              <Form>
                <Form.Group as={Row}>
                  <Form.Label>
                    Mental Demand: {"\n"} How mentally demanding was the task?
                  </Form.Label>
                  <Col xs="9">
                    <Form.Range
                      value={value}
                      onChange={(e) => setValue(e.target.value)}
                    />
                  </Col>
                  <Col xs="3">
                    <Form.Control value={value} />
                  </Col>
                </Form.Group>
              </Form>
            </div>
          </Modal.Body>
          <Modal.Footer></Modal.Footer>
        </Modal>
      </div>
    );
  };

  render() {
    return this.displayModal();
  }
}

export default Survey;
