import React, { Component } from "react";
import { Form } from "react-bootstrap";

class VideoCap extends Component {
  state = {};

  update_answers = (event) => {
    this.setAnswer(event.target.value);
    this.props.update_results(this.state.results);
  };

  render() {
    return (
      <div>
        <Form>
          <Form.Group
            className="mb-3"
            controlId={"Form.Question" + this.props.current_question.toString()}
          >
            <Form.Label>
              <div className="ratio ratio-16x9">
                <iframe
                  src={this.props.config["source"]}
                  title="YouTube video"
                  allowFullScreen
                ></iframe>
              </div>
              <div>Please transcribe the following video into text</div>
            </Form.Label>
            <Form.Control
              as="textarea"
              rows={8}
              onChange={this.update_answers}
              disabled={this.props.disabled}
            />
          </Form.Group>
        </Form>
      </div>
    );
  }
}

export default VideoCap;
